components.dataSource:
=([
    {
        "x": "À évaluer",
        "data1": $d('local-storage-user_input').filter(criteria => criteria.etat === "A_EVALUER").length,
    },
    {
        "x": "Conforme",
        "data1": $d('local-storage-user_input').filter(criteria => criteria.etat === "CONFORME").length,
    },
    {
        "x": "Non conforme",
        "data1": $d('local-storage-user_input').filter(criteria => criteria.etat === "NON_CONFORME").length,
    },
    {
        "x": "Non applicable",
        "data1": $d('local-storage-user_input').filter(criteria => criteria.etat === "NON_APPLICABLE").length,
    }
])

components.init:
this.define("getBadge", (etat) => {
    if (etat === "A_EVALUER") {
        return (`
            <div class="badge badge-secondary badge-pill">
                ${$tools.icon('clock')}
            </div>
        `);
    } else if (etat === "CONFORME") {
        return (`
            <div class="badge badge-success badge-pill">
                ${$tools.icon('check')}
            </div>
        `);
    } else if (etat === "NON_CONFORME") {
        return (`
            <div class="badge badge-danger badge-pill">
                ${$tools.icon('x')}
            </div>
        `);
    } else if (etat === "NON_APPLICABLE") {
        return (`
            <div class="badge badge-primary badge-pill">
                ${$tools.icon('dash')}
            </div>
        `);
    }
    return (``);
})
